import React from "react";
import './FeaturedMovie.css';

export default ({item}) => {
    console.log(item);
    let firstDate = new Date(item.first_air_date);
    let genres = [];
    for(let i in item.genres) {
        genres.push( item.genres[i].name );
    }

    let description = item.overview
    if(description.length > 150) {
        description = description.substring(0, 200)+'...';
    }
    return (
        <section className="featured" style={{
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundImage: `url(https://image.tmdb.org/t/p/original${item.backdrop_path})`
        }}>

            <div className="featured--vertical">
                <div className="featured--horizontal">
                    <div className="featured--name">{item.original_name}</div>
                    <div className="featured--info">
                        <div className="featured--point">{item.vote_average} Puntos</div>
                        <div className="featured--year">{firstDate.getFullYear()}</div>
                        <div className="featured--seasons">{item.number_of_seasons} Temporada{item.number_of_seasons !== 1 ? 's' : ''}</div>
                    </div>
                    <div className="featured--description">{item.overview}</div>
                    <div className="featured--buttons">
                        <a href={`url(http://wa.me/51946324747)}`} className='featured--watchbutton'>â–¶ Pruebalo Gratis</a>
                        <a hr ef={`/list/add/${item.id}`} className='featured--mylistbutton'>Mi lista</a>

                    </div>
                    <div className="featured--genres"><strong>Generos:</strong> {genres.join(', ')}</div>

                </div>

            </div>
        </section>

        
    );
}
