import React from "react";
import './FeaturedMovie.css';

export default ({item}) => {
    console.log(item);
    return (
        <section className="featured" style={{
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            backgroundImage: `url(https://image.tmdb.org/t/p/original${item.backdrop_path})`
        }}>

            <div className="featured--vertical">
                <div className="featured--horizontal">
                    <div className="featured--name">{item.orginal_name}</div>
                    <div className="featured--info">
                        <div className="featured--point">{item.vote_average} Puntos</div>
                        <div className="featured--year">2022</div>
                        <div className="featured--seasons">{item.number_of_season} Temporadas{item.number_of_seasons !== 1 ? 's' : ''}</div>
                       
                    </div>

                </div>

            </div>
        </section>

        
    );
}
